    <script>
        $(document).ready(function () {
            $('.next').click(function () {
                let currentSlide = $('.slide.active');
                let nextSlide = currentSlide.next('.slide');

                if (nextSlide.length) {
                    currentSlide.removeClass('active');
                    nextSlide.addClass('active');
                }
            });

            $('.prev').click(function () {
                let currentSlide = $('.slide.active');
                let prevSlide = currentSlide.prev('.slide');

                if (prevSlide.length) {
                    currentSlide.removeClass('active');
                    prevSlide.addClass('active');
                }
            });
        });
    </script>
    <script>
        $(document).ready(function () {
        // Функция для проверки введенного номера
        function checkPhoneNumber() {
            let phoneNumber = $('input[name="question9"]').val();
            // Проверяем, что введено не менее 10 цифр и только цифры
            if (phoneNumber.length >= 10 && /^\d+$/.test(phoneNumber)) {
                $('.submit').prop('disabled', false);  // Разблокировать кнопку
            } else {
                $('.submit').prop('disabled', true);   // Заблокировать кнопку
            }
        }
    
        // Блокируем кнопку при загрузке страницы
        $('.submit').prop('disabled', true);
    
        // Проверяем номер телефона при вводе
        $('input[name="question9"]').on('input', checkPhoneNumber);
    });
    </script>
    <script>
        $(document).ready(function() {
            // Обработка показа/скрытия текстового поля для "Свій варіант"
            $('input[type="radio"]').prop('checked', false); // Сброс радио-кнопок
            $('textarea').val(''); // Сброс textarea
            $('input[type="radio"]').on('change', function() {
                const currentSlide = $(this).closest('.slide');
                const otherOption = $(this).closest('.custom-radio.other');
                
                // Скрывать текстовое поле для всех "Свій варіант", кроме активного
                currentSlide.find('.other-c').hide();
                
                if ($(this).val() === 'other') {
                    otherOption.find('.other-c').show();
                }
            });
        
            let currentSlideIndex = 0;
            const slides = $('.slide');
            slides.hide().eq(currentSlideIndex).show();
        
            // Логика кнопки "Далі"
            $('.next').click(function() {
                const currentSlide = slides.eq(currentSlideIndex);
                const selectedOption = currentSlide.find('input[type="radio"]:checked').val();
                const customOption = currentSlide.find('textarea').val();
        
                // Проверка, если выбран вариант "Свій варіант", но поле пустое
                if (selectedOption === 'other' && (!customOption || customOption.trim() === '')) {
                    alert('Будь ласка, заповніть поле "Свій варіант"');
                    return;
                }
        
                // Переход на следующий слайд
                currentSlideIndex++;
                if (currentSlideIndex < slides.length) {
                    slides.hide().eq(currentSlideIndex).show();
                } else {
                    // Логика завершения опроса
                    console.log('Опрос завершен');
                    // Собрать данные
                    let formData = {};
                    slides.each(function(index) {
                        const selected = $(this).find('input[type="radio"]:checked').val();
                        const customText = $(this).find('textarea').val() || '';
                        formData[`question${index+1}`] = selected === 'other' ? customText : selected;
                    });
                    console.log(formData);
                    // Отправить данные на сервер или завершить опрос
                }
            });
        
            // Логика кнопки "Назад"
            $('.prev').click(function() {
                if (currentSlideIndex > 0) {
                    currentSlideIndex--;
                    slides.hide().eq(currentSlideIndex).show();
                }
            });
        });

    </script>
